#!/usr/bin/ruby

require 'set'

input = (ARGV.empty? ? DATA : ARGF).each_line.map(&:to_s).freeze

# puts input

level = 0
char_pos = 1
found_pos = -1

input[0].chars do |c|
	if c == '('
		level += 1
	elsif c == ')'
		level -= 1
	end
	if found_pos<0 and level==-1
		found_pos = char_pos
	end
	char_pos += 1
end

puts level, found_pos

level2 = input[0].chars.inject(1) { |s, c| s + (c=='('?1:-1) }

found_pos2 = input[0].chars.each_with_index.inject(1) { |sum, pair| 
	c, index = pair
	break index-1 if sum == -1
	sum + ( (c=='(') ? 1 : -1 ) 
}
puts level2, found_pos2


__END__
